require 'test/unit'
require 'imgrb'

class ImgrbTest < Test::Unit::TestCase

  #Grayscale no alpha
  #=============================================================================

  def test_load_1bit_interlaced_grayscale
    img = Imgrb::Image.new("test/png_test_suite/basi0g01.png")
    assert GRAYSCALE_1BIT_ROWS == img.rows #assert_equal results in very long message if assertion fails.
  end

  def test_load_2bit_interlaced_grayscale
