  def test_load_8bit_interlaced_rgb
    img = Imgrb::Image.new("test/png_test_suite/basi2c08.png")
    assert RGB_R_8BIT_ROWS == img.get_channel(0).rows
    assert RGB_G_8BIT_ROWS == img.get_channel(1).rows
    assert RGB_B_8BIT_ROWS == img.get_channel(2).rows
